import React, { useRef, useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import Header from './Header';
import Body from './Body';
import LoginPage from './Loginpage';
import './App.css';

function App() {
    const imageRef = useRef(null);
    const [buttonSize, setButtonSize] = useState(0);
    const [stage, setStage] = useState(0);

    useEffect(() => {
        function updateButtonSize() {
            if (imageRef.current) {
                const width = imageRef.current.clientWidth;
                const minButtonWidth = 120;
                const buttonWidth = Math.max(width * 0.25, minButtonWidth);
                setButtonSize(buttonWidth);
            }
        }

        window.addEventListener('resize', updateButtonSize);
        updateButtonSize();  // 초기에 한 번 실행

        const timers = [
            setTimeout(() => setStage(1), 1000),
            setTimeout(() => setStage(2), 2000),
            setTimeout(() => setStage(3), 4000),
        ];

        return () => {
            window.removeEventListener('resize', updateButtonSize);
            timers.forEach(timer => clearTimeout(timer));
        };
    }, []);

    return (
        <Router>
            <div className="app">
                <Header />
                <Switch>
                    <Route exact path="/" render={() => <Body imageRef={imageRef} buttonSize={buttonSize} stage={stage} />} />
                    <Route path="/login" component={LoginPage} />
                </Switch>
            </div>
        </Router>
    );
}

export default App;
