import React, { useRef, useState } from 'react';
import './Body.css'; // CSS 파일 로드

function Body({ imageRef, buttonSize, stage }) {
    const [isModalOpen, setModalOpen] = useState(false);

    const handleMouseEnter = () => {
        if (imageRef.current) {
            imageRef.current.style.transform = 'scale(1.1) rotateY(20deg)';
        }
    };

    const handleMouseLeave = () => {
        if (imageRef.current) {
            imageRef.current.style.transform = 'scale(1) rotateY(0)';
        }
    };

    const handleImageClick = () => {
        setModalOpen(true); // 모달 상태 변경
    };

    const handleCloseModal = () => {
        setModalOpen(false); // 모달 닫기
    };

    const [buttonWidth, setButtonWidth] = useState(getButtonWidth());

    useEffect(() => {
        function handleResize() {
            setButtonWidth(getButtonWidth());
        }

        window.addEventListener('resize', handleResize);
        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    function getButtonWidth() {
        // 화면 너비에 따라 버튼의 너비를 계산
        const screenWidth = window.innerWidth;
        if (screenWidth < 768) {
            return 50; // 모바일
        } else if (screenWidth < 1024) {
            return 40; // 태블릿
        } else {
            return 30; // 데스크탑
        }
    }

    return (
        <div className="mainImageContainer">
            <img ref={imageRef} src="main.png" alt="Main Content" className="mainImage" />
            <img 
                ref={imageRef}
                src="a.png"
                alt="Interactive Element"
                className="interactiveImage"
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
                onClick={handleImageClick}
            />
            {stage === 1 && <img src="man1.png" alt="Man 1" className="man man1" />}
            {stage === 2 && <img src="man2.png" alt="Man 2" className="man man2" />}
            {stage === 3 && (
                <>
                    <img src="man3.png" alt="Man 3" className="man man3" />
                    <button className="mainImageButton" style={{
                        width: `${buttonWidth}%`, // 동적으로 버튼 너비 조절
                        height: '40px', // 고정 높이
                        fontSize: '1vw', // 뷰포트 너비에 따른 폰트 크기 조절
                        top: '40%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        opacity: 1,
                    }}>
                        내 영상 피드백
                    </button>
                </>
            )}
            {isModalOpen && (
                <div className="modal" onClick={handleCloseModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <span className="close" onClick={handleCloseModal}>&times;</span>
                        <img src="a1.png" alt="Detailed Image" className="modalImage" />
                    </div>
                </div>
            )}
        </div>
    );
}

export default Body;
