import React, { useState, useEffect } from 'react';
import Header from './Header';
import './App.css';

function App() {
    const [imageHeight, setImageHeight] = useState(0);

    useEffect(() => {
        function handleResize() {
            // 이미지의 원본 너비와 높이 비율에 따라 높이를 계산합니다.
            const originalWidth = 1200;
            const originalHeight = 800;
            const newHeight = window.innerWidth * (originalHeight / originalWidth);
            setImageHeight(newHeight);
        }

        // 윈도우 리사이즈 이벤트 리스너를 추가합니다.
        window.addEventListener('resize', handleResize);
        // 초기 높이를 설정합니다.
        handleResize();

        // 컴포넌트가 언마운트 될 때 이벤트 리스너를 제거합니다.
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    return (
        <div className="app">
            <Header />
            <div className="mainImageContainer" style={{ height: `${imageHeight}px` }}>
                <img src="main.png" alt="Main Content" className="mainImage" />
                <button className="mainImageButton">Click Me!</button>
            </div>
        </div>
    );
}

export default App;
