import React, { useRef, useState, useEffect } from 'react';
import Header from './Header';
import Body from './Body';
import './App.css';

function App() {
    const imageRef = useRef(null);
    const [buttonSize, setButtonSize] = useState(0);
    const [stage, setStage] = useState(0); // 애니메이션 단계를 관리하는 상태

    useEffect(() => {
        function updateButtonSize() {
            const width = imageRef.current.clientWidth;
            const minButtonWidth = 120;  // 최소 버튼 크기
            const buttonWidth = Math.max(width * 0.25, minButtonWidth);  // 버튼 너비 계산
            setButtonSize(buttonWidth);
        }

        if (imageRef.current) {
            imageRef.current.addEventListener('load', updateButtonSize);
        }

        window.addEventListener('resize', updateButtonSize);
        updateButtonSize();  // 초기 버튼 크기 설정

        const timers = [
            setTimeout(() => setStage(1), 1000),  // man1.png 표시
            setTimeout(() => setStage(2), 2000),  // man2.png 표시 및 애니메이션
            setTimeout(() => setStage(3), 4000),  // man3.png 및 버튼 표시
        ];

        return () => {
            if (imageRef.current) {
                imageRef.current.removeEventListener('load', updateButtonSize);
            }
            window.removeEventListener('resize', updateButtonSize);
        };
    }, []);

    return (
        <div className="app">
            <Header />
            <Body imageRef={imageRef} buttonSize={buttonSize} stage={stage} />
        </div>
    );
}

export default App;
