import React from 'react';
import { Card, CardActionArea, CardContent, CardMedia, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import '../assets/css/common.css'; // 공통 CSS 불러오기

function MyPage() {
    const movieList = [
        { id: 'movie1', title: '24/', description: 'Description for Movie 1' },
        { id: 'movie2', title: 'Movie 2', description: 'Description for Movie 2' },
        { id: 'movie3', title: 'Movie 3', description: 'Description for Movie 3' },
    ]; // 비디오 목록

    return (
        <div className="page">
            <img src="main.png" alt="Main" className="main-image" /> {/* 메인 이미지 배경 */}
            <div className="thumbnail-grid">
                {movieList.map((movie, index) => (
                    <ThumbNail key={index} index={index} movie={movie} />
                ))}
            </div>
        </div>
    );
}

function ThumbNail({ index, movie }) {
    const navigate = useNavigate();
    const handleClick = () => {
        navigate(`/mymovie/${movie.id}`);
    };
    return (
        <Card sx={{ maxWidth: 345 }} onClick={handleClick}>
            <CardActionArea>
                <CardMedia
                    component="img"
                    height="240"
                    image={`/images/image${index}.png`}
                    alt={movie.title}
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {movie.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        {movie.description}
                    </Typography>
                </CardContent>
            </CardActionArea>
        </Card>
    );
}

export default MyPage;
