import React, { useRef, useState, useEffect } from 'react';
import Header from './Header';
import Body from './Body';
import './App.css';

function App() {
    const imageRef = useRef(null);
    const [buttonSize, setButtonSize] = useState(0); // 버튼 크기를 너비만 저장하는 상태로 변경
    const [buttonVisible, setButtonVisible] = useState(false);

    useEffect(() => {
        function updateButtonSize() {
            const width = imageRef.current.clientWidth; // 이미지 너비 가져오기
            const minButtonWidth = 120;
            const buttonWidth = width * 0.25; // 이미지 너비의 20%를 버튼 너비로 설정
            setButtonSize(buttonWidth);
        }

        if (imageRef.current) {
            imageRef.current.addEventListener('load', updateButtonSize);
        }

        window.addEventListener('resize', updateButtonSize);

        updateButtonSize(); // 초기 버튼 크기 설정

        setTimeout(() => {
            setButtonVisible(true);
        }, 1000);  // 1초 후 버튼 표시

        return () => {
            if (imageRef.current) {
                imageRef.current.removeEventListener('load', updateButtonSize);
            }
            window.removeEventListener('resize', updateButtonSize);
        };
    }, []);

    const fontSize = Math.max(buttonSize / 10, 12);  

    return (
        <div className="app">
            <Header />
            <Body />
        </div>
    );
}

export default App;
