import React, { useRef, useState, useEffect } from 'react';
import { Slider, Typography, Box } from '@mui/material';
import { useParams } from 'react-router-dom';
import '../assets/css/MyMovie.css';

function MyMovie() {
    const { id } = useParams();
    const videoRef = useRef(null);
    const controlsRef = useRef(null);
    const [currentTime, setCurrentTime] = useState(0);
    const [duration, setDuration] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);

    useEffect(() => {
        const video = videoRef.current;

        const handleTimeUpdate = () => {
            setCurrentTime(video.currentTime);
        };

        const handleDurationChange = () => {
            setDuration(video.duration);
        };

        video.addEventListener('timeupdate', handleTimeUpdate);
        video.addEventListener('loadedmetadata', handleDurationChange);

        return () => {
            video.removeEventListener('timeupdate', handleTimeUpdate);
            video.removeEventListener('loadedmetadata', handleDurationChange);
        };
    }, []);

    const handlePlayPause = () => {
        const video = videoRef.current;
        if (isPlaying) {
            video.pause();
        } else {
            video.play();
        }
        setIsPlaying(!isPlaying);
    };

    const handleSliderChange = (event, newValue) => {
        const video = videoRef.current;
        video.currentTime = newValue;
        setCurrentTime(newValue);
    };

    const formatTime = (time) => {
        const minutes = Math.floor(time / 60);
        const seconds = Math.floor(time % 60);
        return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`;
    };

    const showControls = () => {
        controlsRef.current.classList.add('show');
    };

    const hideControls = () => {
        controlsRef.current.classList.remove('show');
    };

    return (
        <Box className="video-container" onMouseEnter={showControls} onMouseLeave={hideControls}>
            <img src="main.png" alt="Main Background" className="background-image" />
            <video
                ref={videoRef}
                className="video-element"
                poster={`/images/image${id}.png`}
            >
                <source src={`/movie/${id}.mp4`} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
            <Box className="controls" ref={controlsRef}>
                <button
                    className="control-button"
                    onClick={handlePlayPause}
                >
                    {isPlaying ? '⏸' : '▶'}
                </button>
                <Box className="slider-container">
                    <Typography className="time-text">
                        {formatTime(currentTime)}
                    </Typography>
                    <Slider
                        value={currentTime}
                        min={0}
                        max={duration}
                        onChange={handleSliderChange}
                        className="slider"
                    />
                    <Typography className="time-text">
                        {formatTime(duration)}
                    </Typography>
                </Box>
            </Box>
        </Box>
    );
}

export default MyMovie;
