import React, { useRef, useState, useEffect } from 'react';
import '../assets/css/MyMovie.css';

function MyMovie() {
    const videoRef = useRef(null);
    const [isPlaying, setIsPlaying] = useState(false);
    const [currentTime, setCurrentTime] = useState(0);
    const [duration, setDuration] = useState(0);
    const [showControls, setShowControls] = useState(false);

    const togglePlayPause = () => {
        if (isPlaying) {
            videoRef.current.pause();
        } else {
            videoRef.current.play();
        }
        setIsPlaying(!isPlaying);
    };

    const handleTimeUpdate = () => {
        setCurrentTime(videoRef.current.currentTime);
    };

    const handleLoadedMetadata = () => {
        setDuration(videoRef.current.duration);
    };

    const handleMouseEnter = () => {
        setShowControls(true);
    };

    const handleMouseLeave = () => {
        setShowControls(false);
    };

    const formatTime = (time) => {
        const minutes = Math.floor(time / 60);
        const seconds = Math.floor(time % 60);
        return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
    };

    const handleProgressClick = (e) => {
        const progressBar = e.target;
        const rect = progressBar.getBoundingClientRect();
        const offsetX = e.clientX - rect.left;
        const newTime = (offsetX / progressBar.offsetWidth) * duration;
        videoRef.current.currentTime = newTime;
        setCurrentTime(newTime);
    };

    return (
        <div
            className="video-container"
            style={{
                backgroundImage: `url(${process.env.PUBLIC_URL}/main.png)`,
            }}
        >
            <div className="video-wrapper" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                <video
                    ref={videoRef}
                    className="video-element"
                    onTimeUpdate={handleTimeUpdate}
                    onLoadedMetadata={handleLoadedMetadata}
                    controls={false}
                >
                    <source src={`/movie/movie1.mp4`} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
                <div className={`controls ${showControls ? 'show' : ''}`}>
                    <button className="control-button" onClick={togglePlayPause}>
                        {isPlaying ? '⏸' : '▶️'}
                    </button>
                    <div className="progress-bar" onClick={handleProgressClick}>
                        <div
                            className="progress"
                            style={{ width: `${(currentTime / duration) * 100}%` }}
                        ></div>
                    </div>
                    <div className="time-container">
                        <span className="time-text">{formatTime(currentTime)}</span>
                        <span className="time-text">{formatTime(duration)}</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default MyMovie;
