import React, { useRef, useState, useEffect } from 'react';
import './Body.css'; // CSS 파일 로드

function Body({ stage }) {
    const [isModalOpen, setModalOpen] = useState(false);
    const [selectedImageIndex, setSelectedImageIndex] = useState(null);
    const refs = useRef([...Array(6)].map(() => React.createRef()));

    const handleMouseEnter = (ref) => {
        if (ref.current) {
            ref.current.style.transform = 'scale(1.1) rotateY(20deg)';
        }
    };

    const handleMouseLeave = (ref) => {
        if (ref.current) {
            ref.current.style.transform = 'scale(1) rotateY(0)';
        }
    };

    const handleImageClick = (index) => {
        setSelectedImageIndex(index);
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setModalOpen(false);
    };

    return (
        <div className="mainImageContainer">
            <img ref={refs.current[0]} src="main.png" alt="Main Content" className="mainImage" />
            {[...Array(6).keys()].map(index => (
                <div className="imageButtonContainer" key={index}>
                    <img 
                        ref={refs.current[index]}
                        src={`${String.fromCharCode(97 + index)}.png`}
                        alt={`Interactive Element ${String.fromCharCode(97 + index)}`}
                        className={`interactiveImage interactiveImage-${String.fromCharCode(97 + index)}`}
                        onMouseEnter={() => handleMouseEnter(refs.current[index])}
                        onMouseLeave={() => handleMouseLeave(refs.current[index])}
                        onClick={() => handleImageClick(index)}
                    />
                    <div className="clickPrompt">
                        <span className="clickArrow">&#8594;</span> 클릭!
                    </div>
                </div>
            ))}
            {stage === 1 && <img src="man1.png" alt="Man 1" className="man man1" />}
            {stage === 2 && <img src="man2.png" alt="Man 2" className="man man2" />}
            {stage === 3 && (
                <>
                    <img src="man3.png" alt="Man 3" className="man man3" />
                    <button className="mainImageButton">
                        내 영상 피드백
                    </button>
                </>
            )}
            {isModalOpen && (
                <div className="modal" onClick={handleCloseModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <span className="close" onClick={handleCloseModal}>&times;</span>
                        <img src={`${String.fromCharCode(97 + selectedImageIndex)}1.png`} alt="Detailed Image" className="modalImage" />
                    </div>
                </div>
            )}
        </div>
    );
}

export default Body;
