import React from 'react';
import './Body.css'; // CSS 파일 로드

function Body({ imageRef, buttonSize, fontSize, buttonVisible }) {
    // 버튼 초기 투명도 설정
    const initialStyle = {
        width: `${buttonSize}px`, 
        height: `${buttonSize / 3}px`,
        fontSize: `${fontSize}px`,
        top: '50%', 
        left: '50%', 
        transform: 'translate(-50%, -50%)',
        opacity: 0, // 초기 투명도는 0
        transition: 'opacity 2s ease-in-out'
    };

    return (
        <div className="mainImageContainer">
            <img ref={imageRef} src="main.png" alt="Main Content" className="mainImage" />
            <button className="mainImageButton" style={buttonVisible ? {...initialStyle, opacity: 1} : initialStyle}>
                내 영상 피드백
            </button>
        </div>
    );
}

export default Body;
