import React, { useRef, useState, useEffect } from 'react';
import Header from './Header';
import './App.css';

function App() {
    const imageRef = useRef(null);
    const [buttonSize, setButtonSize] = useState({width: 0, height: 0}); // 버튼 크기 상태

    useEffect(() => {
        function updateButtonSize() {
            const width = imageRef.current.clientWidth; // 이미지 너비 가져오기
            const buttonWidth = width * 0.1; // 이미지 너비의 10%를 버튼 너비로 설정
            const buttonHeight = buttonWidth * 0.5; // 버튼 높이는 너비의 50%
            setButtonSize({width: buttonWidth, height: buttonHeight});
        }

        if (imageRef.current) {
            imageRef.current.addEventListener('load', updateButtonSize);
        }

        window.addEventListener('resize', updateButtonSize);

        updateButtonSize(); // 초기 버튼 크기 설정

        return () => {
            if (imageRef.current) {
                imageRef.current.removeEventListener('load', updateButtonSize);
            }
            window.removeEventListener('resize', updateButtonSize);
        };
    }, []);

    return (
        <div className="app">
            <Header />
            <div className="mainImageContainer">
                <img ref={imageRef} src="main.png" alt="Main Content" className="mainImage" />
                <button className="mainImageButton" style={{ 
                    width: `${buttonSize.width}px`, 
                    height: `${buttonSize.height}px`, 
                    top: `calc(50% - ${buttonSize.height / 2}px)`, 
                    left: `calc(50% - ${buttonSize.width / 2}px)` 
                }}>
                    Click Me!
                </button>
            </div>
        </div>
    );
}

export default App;
