import React, { useRef, useState, useEffect } from 'react';
import Header from './Header';
import './App.css';

function App() {
    const imageRef = useRef(null);
    const [buttonSize, setButtonSize] = useState(0);
    const [buttonVisible, setButtonVisible] = useState(false);  // 버튼 표시 상태

    useEffect(() => {
        function updateButtonSize() {
            const width = imageRef.current.clientWidth;
            const minButtonWidth = 120;
            const buttonWidth = Math.max(width * 0.2, minButtonWidth);
            setButtonSize(buttonWidth);
        }

        if (imageRef.current) {
            imageRef.current.addEventListener('load', updateButtonSize);
        }

        window.addEventListener('resize', updateButtonSize);
        updateButtonSize();

        // 애니메이션 이후 버튼을 보여주는 로직
        setTimeout(() => {
            setButtonVisible(true);
        }, 1000);  // 1초 후 버튼 표시

        return () => {
            if (imageRef.current) {
                imageRef.current.removeEventListener('load', updateButtonSize);
            }
            window.removeEventListener('resize', updateButtonSize);
        };
    }, []);

    return (
        <div className="app">
            <Header />
            <div className="mainImageContainer">
                <img ref={imageRef} src="main.png" alt="Main Content" className="mainImage" />
                {buttonVisible && <button className="mainImageButton" style={{ 
                    width: `${buttonSize}px`, 
                    height: `${buttonSize / 3}px`,
                    fontSize: `${buttonSize / 10}px`,
                    top: '50%', 
                    left: '50%', 
                    transform: 'translate(-50%, -50%)',
                    opacity: buttonVisible ? 1 : 0,
                    transition: 'opacity 0.5s ease-in-out'
                }}>
                    내 영상 피드백하기
                </button>}
            </div>
        </div>
    );
}

export default App;
