import React, { useRef, useState, useEffect } from 'react';
import './Body.css'; // CSS 파일 로드

function Body({ imageRef, stage }) {
    const [isModalOpen, setModalOpen] = useState(false);
    const [selectedImageIndex, setSelectedImageIndex] = useState(null);

    useEffect(() => {
        const handleResize = () => {
            // Resize handler if needed for other purposes
        };
        window.addEventListener('resize', handleResize);
        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    const handleMouseEnter = () => {
        if (imageRef.current) {
            imageRef.current.style.transform = 'scale(1.1) rotateY(20deg)';
        }
    };

    const handleMouseLeave = () => {
        if (imageRef.current) {
            imageRef.current.style.transform = 'scale(1) rotateY(0)';
        }
    };

    const handleImageClick = (index) => {
        setSelectedImageIndex(index); // 클릭된 이미지의 인덱스 설정
        setModalOpen(true); // 모달 상태 변경
    };

    const handleCloseModal = () => {
        setModalOpen(false); // 모달 닫기
    };

    return (
        <div className="mainImageContainer">
            <img ref={imageRef} src="main.png" alt="Main Content" className="mainImage" />
            {[...Array(6).keys()].map(index => (
                <img 
                    key={index}
                    src={`${String.fromCharCode(97 + index)}.png`} // a.png부터 f.png까지 순차적으로 로드
                    alt={`Interactive Element for ${String.fromCharCode(97 + index)}`}
                    className={`interactiveImage position-${index}`} // 위치를 나타내는 클래스 추가
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                    onClick={() => handleImageClick(index)} // 클릭 핸들러에 이미지 인덱스 전달
                />
            ))}
            {stage === 1 && <img src="man1.png" alt="Man 1" className="man man1" />}
            {stage === 2 && <img src="man2.png" alt="Man 2" className="man man2" />}
            {stage === 3 && (
                <>
                    <img src="man3.png" alt="Man 3" className="man man3" />
                    <button className="mainImageButton">
                        내 영상 피드백
                    </button>
                </>
            )}
            {isModalOpen && (
                <div className="modal" onClick={handleCloseModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <span className="close" onClick={handleCloseModal}>&times;</span>
                        <img src={`${String.fromCharCode(97 + selectedImageIndex)}1.png`} alt="Detailed Image" className="modalImage" />
                    </div>
                </div>
            )}
        </div>
    );
}

export default Body;
