import React from 'react';
import './Body.css'; // CSS 파일 로드

function Body({ imageRef, buttonSize, stage }) {
    return (
        <div className="mainImageContainer">
            <img ref={imageRef} src="main.png" alt="Main Content" className="mainImage" />
            {stage === 2 && <img src="man1.png" alt="Man 1" className="man man1" />}
            {stage === 3 && <img src="man2.png" alt="Man 2" className="man man2" />}
            {stage === 5 && <button className="mainImageButton" style={{
                width: `${buttonSize}px`,
                height: `${buttonSize / 3}px`,
                fontSize: `${buttonSize / 10}px`,
                top: '50%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
                opacity: 1,
                transition: 'opacity 2s ease-in-out'
            }}>
                내 영상 피드백하기
            </button>}
        </div>
    );
}

export default Body;
